mapper.select:
SELECT * FROM AC_SALESTATEMENT


S_NUM=#{select}


S_COMPANY=#{select}


S_EMPLOYEE=#{select}

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT *
FROM APPROVALDOCU
WHERE AP_FROMAPPROVER = #{hrCode} AND AP_CCODE = #{cCode}
) A
)
WHERE RN BETWEEN #{start} AND #{end}

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT *
FROM APPROVALDOCU
WHERE AP_TOAPPROVER = #{hrCode} AND AP_CCODE = #{cCode} AND AP_STATUS != '4'
) A
)
WHERE RN BETWEEN #{start} AND #{end}

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT *
FROM AC_JNOB
WHERE J_NONE = #{hrCode} AND J_GRADE = '0' AND J_CCODE = #{cCode}
) A
)
WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE AC_JNOB
SET J_GRADE = #{j_grade}
WHERE J_DOCUNUM
= #{j_docunum} AND J_CCODE = #{j_ccode}

UPDATE APPROVALDOCU
SET AP_STATUS = #{ap_status},
AP_TOAPPROVER = #{ap_toapprover}
WHERE AP_DOCUNUM = #{ap_docunum} AND
AP_CCODE = #{ap_ccode}

UPDATE AC_JNOB
SET J_GRADE = '4', J_REASION =
#{j_reasion}
WHERE J_DOCUNUM = #{j_docunum} AND J_CCODE = #{j_ccode}

UPDATE APPROVALDOCU
SET AP_STATUS = '4'
WHERE AP_DOCUNUM = #{ap_docunum} AND AP_CCODE = #{ap_ccode}
